const templates = {
    wrong: (choiceText, questionExpl) => `Die Option ‚Äû${choiceText}‚Äú ist nicht korrekt, weil ${questionExpl ? questionExpl.replace(/\.$/, '') + '.' : 'sie nicht das gefragte Kriterium erf√ºllt.'}`,
    correct: (questionExpl) => questionExpl || 'Diese Option entspricht exakt dem gefragten Konzept.'
};

export function enrichQuestion(q) {
    const defaultCorrectExpl = templates.correct(q.explanation);
    const enrichedChoices = q.choices.map((choice, idx) => {
        let choiceText = typeof choice === 'string' ? choice : choice.text;
        let baseExpl = (typeof choice === 'object' && choice.explanation) ? choice.explanation : '';
        if (!baseExpl || baseExpl.trim() === '') {
            baseExpl = idx === q.answer ? defaultCorrectExpl : templates.wrong(choiceText, q.explanation);
        }
        return { text: choiceText, explanation: baseExpl };
    });
    const correctExpl = enrichedChoices[q.answer].explanation;
    return { ...q, choices: enrichedChoices, explanation: correctExpl };
}

